// Unique Identifiers for the various Teach and Test Tutorials.
const int TRAINING_SESSION_INVALID                   = 0;
const int TRAINING_SESSION_TACTICAL_VIEW             = 1;
const int TRAINING_SESSION_MOVE_COMMANDS             = 2;
const int TRAINING_SESSION_FOLLOWERS_AND_TACTICS     = 3;
const int TRAINING_SESSION_LEVEL_UP                  = 4;
const int TRAINING_SESSION_EQUIPMENT                 = 5;
const int TRAINING_SESSION_HOTBAR                    = 6;

void BeginTrainingMode(int a_nTrainingSessionId)
{
    //This will fire a: EVENT_TYPE_TRAINING_BEGIN event.
    SetTrainingMode(a_nTrainingSessionId);
}

void EndTrainingMode()
{
    SetTrainingMode(TRAINING_SESSION_INVALID);
}
