    WRK_TALENTS_SHALE[1] = WRK_ABILITY_SHALE_QUAKE;
    WRK_TALENTS_SHALE[2] = WRK_ABILITY_SHALE_SLAM;
    WRK_TALENTS_SHALE[3] = WRK_ABILITY_SHALE_PULVERIZING_BLOWS;
    WRK_TALENTS_SHALE[4] = WRK_ABILITY_SHALE_REGENERATING_BURST;
    WRK_TALENTS_SHALE[5] = WRK_ABILITY_SHALE_STONE_ROAR;
    WRK_TALENTS_SHALE[6] = WRK_ABILITY_SHALE_BELLOW;
    WRK_TALENTS_SHALE[7] = WRK_ABILITY_SHALE_STONEHEART;
    WRK_TALENTS_SHALE[8] = WRK_ABILITY_SHALE_ROCK_BARRAGE;
    WRK_TALENTS_SHALE[9] = WRK_ABILITY_SHALE_EARTHEN_GRASP;
    WRK_TALENTS_SHALE[10] = WRK_ABILITY_SHALE_HURL_ROCK;
    WRK_TALENTS_SHALE[11] = WRK_ABILITY_SHALE_ROCK_MASTERY;
    WRK_TALENTS_SHALE[12] = WRK_ABILITY_SHALE_SUPERNATURAL_RESILIENCE;
    WRK_TALENTS_SHALE[13] = WRK_ABILITY_SHALE_RENEWED_ASSAULT;
    WRK_TALENTS_SHALE[14] = WRK_ABILITY_SHALE_INNER_RESERVES;
    WRK_TALENTS_SHALE[15] = WRK_ABILITY_SHALE_STONE_AURA;
    WRK_TALENTS_SHALE[16] = ABILITY_TALENT_DEATH_BLOW;
    WRK_TALENTS_SHALE[17] = ABILITY_TALENT_BRAVERY;
    WRK_TALENTS_SHALE[18] = ABILITY_TALENT_THREATEN;
    WRK_TALENTS_SHALE[19] = ABILITY_TALENT_POWERFUL;
    WRK_TALENTS_SHALE[20] = ABILITY_TALENT_PERFECT_STRIKING;
    WRK_TALENTS_SHALE[21] = ABILITY_TALENT_DISENGAGE;
    WRK_TALENTS_SHALE[22] = ABILITY_TALENT_TAUNT;
    WRK_TALENTS_SHALE[23] = ABILITY_TALENT_PRECISE_STRIKING;    

    ////
    //  SPECIALIZATION
    ////

    // Remove all the effects so they won't get stuck on
    // the character due to an activated ability that gets
    // removed. Injuries should remain.
    RemoveAllEffects(oCharacter);

    if ( GetCreatureCoreClass(oCharacter) == CLASS_WIZARD )
    {
        // Free up the mage talents
        WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_MAGE), WRK_TALENTS_MAGE, oCharacter);

        // Specializations
        WRK_FreeSpecialization(oCharacter, ABILITY_SPELL_HIDDEN_ARCANE_WARRIOR);
        WRK_FreeSpecialization(oCharacter, ABILITY_SPELL_HIDDEN_BLOODMAGE);
        WRK_FreeSpecialization(oCharacter, ABILITY_SPELL_HIDDEN_SHAPESHIFTER);
        WRK_FreeSpecialization(oCharacter, ABILITY_SPELL_HIDDEN_SPIRIT_HEALER);
    }
    else if ( GetCreatureCoreClass(oCharacter) == CLASS_ROGUE )
    {
        // Free up the rogue talents
        WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_ROGUE), WRK_TALENTS_ROGUE, oCharacter);

        // Specializations
        WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_ASSASSIN);
        WRK_FreeSpecialization(oCharacter, ABILITY_SPELL_HIDDEN_BARD);
        WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_DUELIST);
        WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_RANGER);

        // Let's make sure we are not stuck in stealth
        if (IsStealthy(oCharacter) == TRUE)
            DropStealth(oCharacter);
    }
    else if ( GetCreatureCoreClass(oCharacter) == CLASS_WARRIOR )
    {
        // Shale is a warrior too, could check for race too,
        // but this is more specific. Shale has no specialization
        if ( GetName(oCharacter) == "Shale" )
        {
            WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_SHALE), WRK_TALENTS_SHALE, oCharacter);
        }
        else
        {
            // Free up the warrior talents
            WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_WARRIOR), WRK_TALENTS_WARRIOR, oCharacter);

            // Specializations
            WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_CHAMPION);
            WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_TEMPLAR);
            WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_BERSERKER);
            WRK_FreeSpecialization(oCharacter, ABILITY_TALENT_HIDDEN_REAVER);
        }
    }
    else if ( GetCreatureCoreClass(oCharacter) == CLASS_DOG )
    {
        // Free up the dog talents
        WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_DOG), WRK_TALENTS_DOG, oCharacter);
    }
    else if ( GetCreatureCoreClass(oCharacter) == CLASS_SHALE )
    {
        // Free up shale's talents
        WRK_LOOP_SPELL(0,GetArraySize(WRK_TALENTS_SHALE), WRK_TALENTS_SHALE, oCharacter);
    }

} // ! WRK_RESPEC_SPELLS
